DROP TABLE League_standing;
CREATE TABLE League_standing(
LID VARCHAR(5) NOT NULL,
Season VARCHAR(4) NOT NULL,
Rank VARCHAR(2),
Club VARCHAR(100) NOT NULL,
Pld INTEGER,
Win INTEGER,
Draw INTEGER,
Lost INTEGER,
GF INTEGER,
GA INTEGER,
GD INTEGER,
Points INTEGER,
CONSTRAINT standingKey PRIMARY KEY(LID, Season, Club)
);




